windowlist.script:
					function onSortCompare(w1, w2)
						local returnValue = false;
												
						local circle1 = tonumber(w1.circle.getValue());
						local circle2 = tonumber(w2.circle.getValue());
						
						if circle1 == nil then
							circle1 = 0;
						end
						if circle2 == nil then
							circle2 = 0;
						end
							
 
						if circle1 == circle2 then
							returnValue = w1.name.getValue() > w2.name.getValue();
						else
							returnValue = circle1 > circle2;
						end
 
						return returnValue;
					end
					function onDrop(x, y, draginfo)
						if draginfo.isType("shortcut") then
							local class, datasource = draginfo.getShortcutData();
							local source = draginfo.getDatabaseNode();

							if source and class == "reftalentfull" then
								local newentry = addCustomTal_Skill(source);
							elseif source and class == "refskillfull" then
								local newentry = addCustomSkill(source);
							else
								return nil;
							end
							return true;
						end
					end

					function addCustomTal_Skill(source)
						local newentry = createWindow();
						newentry.name.setValue(source.createChild("name", "string").getValue());
						local newnode = newentry.getDatabaseNode();
						newnode.createChild("attribname", "string").setValue(source.createChild("attribname", "string").getValue());
						newnode.createChild("description", "string").setValue(source.createChild("description", "string").getValue());
						newnode.createChild("action", "number").setValue(source.createChild("action", "number").getValue());
						newnode.createChild("generalskill", "number").setValue(source.createChild("skilluse", "number").getValue());
						newnode.createChild("strain", "number").setValue(source.createChild("strain", "number").getValue());
					end

					function addCustomSkill(source)
						local newentry = createWindow();
						newentry.name.setValue(source.createChild("name", "string").getValue());
						local newnode = newentry.getDatabaseNode();
						newnode.createChild("attribname", "string").setValue(source.createChild("attribname", "string").getValue());
						newnode.createChild("description", "string").setValue(source.createChild("description", "string").getValue());
						newnode.createChild("action", "number").setValue(source.createChild("action", "number").getValue());
						newnode.createChild("generalskill", "number").setValue(source.createChild("generalskill", "number").getValue());
						newnode.createChild("knowledge", "number").setValue(source.createChild("knowledge", "number").getValue());
						newnode.createChild("strain", "number").setValue(source.createChild("strain", "number").getValue());
					end

					function onSortCompare(w1, w2)
						local returnValue = false;
						returnValue = w1.language.getValue() > w2.language.getValue();
						return returnValue;
					end

textlistitemvalue.script:
function onLoseFocus()
	super.onLoseFocus();
	window.windowlist.applySort();
end
function onDoubleClick()
	win = Interface.openWindow("skillfull",window.getDatabaseNode());
end

modifiernumber.script:
					function onDrag(button,x,y,dragdata)
						dragdata.setType("dice");
						dragdata.setSlot(1);
						dice = {};
						mod = 0;
						dice, mod = Step.getStepDice(getValue());
						dragdata.setDieList(dice);
						
						dragdata.setDescription(window.name.getValue() .. " (Step " .. getValue() .. ")" );
						dragdata.setNumberData(mod);
						
						return true;
					end	

					function onDoubleClick()
						dice = {};
						mod = 0;
						dice, mod = Step.getStepDice(getValue());
						ChatManager.control.throwDice("dice",dice, mod, window.name.getValue() .. " (Step " .. getValue() .. ")");
						if window.strain.getValue() > 0 then
							window.strain.onDoubleClick();
						end
					end
					function onHover(state)
						if state then
							setColor("#FF228822");
							setFrame("sheetfocus", 3, 3, 3, 3);
							window.setFrame("rowshade");
						else
							if hasFocus() == false then
								setColor(nil);
								setFrame("modifier", 3, 3, 3, 3);
								window.setFrame(nil);
							end
						end
					end
					function onValueChanged()
						updateListBonuses("attacks", window.name.getValue(), "attackbonusonesourcename", "attackbonusonestep");
						updateListBonuses("attacks", window.name.getValue(), "attackbonustwosourcename", "attackbonustwostep");
						updateListBonuses("attacks", window.name.getValue(), "attackbonusthreesourcename", "attackbonusthreestep");
						updateListBonuses("attacks", window.name.getValue(), "attackbonusfoursourcename", "attackbonusfourstep");
						updateListBonuses("attacks", window.name.getValue(), "attackbonusfivesourcename", "attackbonusfivestep");
						updateListBonuses("attacks", window.name.getValue(), "attackbonussixsourcename", "attackbonussixstep");
						updateListBonuses("attacks", window.name.getValue(), "attackbonussevensourcename", "attackbonussevenstep");

						updateListBonuses("attacks", window.name.getValue(), "damagebonusonesourcename", "damagebonusonestep");
						updateListBonuses("attacks", window.name.getValue(), "damagebonustwosourcename", "damagebonustwostep");
						updateListBonuses("attacks", window.name.getValue(), "damagebonusthreesourcename", "damagebonusthreestep");
						updateListBonuses("attacks", window.name.getValue(), "damagebonusfoursourcename", "damagebonusfourstep");
						updateListBonuses("attacks", window.name.getValue(), "damagebonusfivesourcename", "damagebonusfivestep");
						updateListBonuses("attacks", window.name.getValue(), "damagebonussixsourcename", "damagebonussixstep");
						updateListBonuses("attacks", window.name.getValue(), "damagebonussevensourcename", "damagebonussevenstep");
					end

numberfield.script:
					function onDoubleClick()
						if User.isHost() then
							userguy = GmIdentityManager.getCurrent();
						else
							userguy = User.getIdentityLabel();
						end
						window.windowlist.window.getDatabaseNode().getChild("health.damage").setValue(window.windowlist.window.getDatabaseNode().getChild("health.damage").getValue() + getValue());
						ChatManager.control.deliverMessage({ font = "narratorfont", text = "[" .. userguy .. "]" .. ": Strain Damage [" .. window.name.getValue() .. " ( " .. getValue() ..")] --> " .. window.windowlist.window.getDatabaseNode().getChild("name").getValue(), mode = "emote"});

						if getValue() >= window.windowlist.window.getDatabaseNode().getChild("health.woundthreshold.total").getValue() then
							window.windowlist.window.getDatabaseNode().getChild("health.wounds").setValue(window.windowlist.window.getDatabaseNode().getChild("health.wounds").getValue() + 1);
							ChatManager.control.deliverMessage({ font = "narratorfont", text = "[" .. userguy .. "]" .. ": Strain Wound [" .. window.name.getValue() .. "] --> " .. window.windowlist.window.getDatabaseNode().getChild("name").getValue(), mode = "emote"});
						end
						if window.windowlist.window.getDatabaseNode().getChild("totdamage").getValue() >= window.windowlist.window.getDatabaseNode().getChild("health.death.total").getValue() then
							ChatManager.control.deliverMessage({ font = "narratorfont", text = "[" .. userguy .. "]" .. ": Death [" .. window.name.getValue() .. "] --> " .. window.windowlist.window.getDatabaseNode().getChild("name").getValue(), mode = "emote"});
						elseif window.windowlist.window.getDatabaseNode().getChild("totdamage").getValue() >= window.windowlist.window.getDatabaseNode().getChild("health.unconscious.total").getValue() then
							ChatManager.control.deliverMessage({ font = "narratorfont", text = "[" .. userguy .. "]" .. ": Unconscious [" .. window.name.getValue() .. "] --> " .. window.windowlist.window.getDatabaseNode().getChild("name").getValue(), mode = "emote"});
						end
							
					end
					function onHover(state)
						if state then
							setColor("#FF882222");
							setFrame("sheetfocus", 3, 3, 3, 3);
							window.setFrame("rowshade");
						else
							if hasFocus() == false then
								setColor(nil);
								setFrame("modifier", 3, 3, 3, 3);
								window.setFrame(nil);
							end
						end
					end
					function onWheel()
						return false;
					end

buttoncontrol.script:
function onButtonPress()
	win = Interface.openWindow("skillfull",window.getDatabaseNode());
end
function onHover(state)
	if state then
		window.setFrame("rowshade");
	else
		window.setFrame(nil);
	end
end

